param_list(wg_struct_sick,run,[pEmplKey-154242641,pFirstMoveKey-154242645,pQuery-usr_wg_MovementLine/15,pSQL-"SELECT ml.USR$EMPLKEY, ml.DOCUMENTKEY, ml.USR$FIRSTMOVE AS FirstMoveKey, EXTRACT(YEAR FROM ml.USR$DATEBEGIN) AS MoveYear, EXTRACT(MONTH FROM ml.USR$DATEBEGIN) AS MoveMonth, ml.USR$DATEBEGIN, ml.USR$SCHEDULEKEY, ml.USR$MOVEMENTTYPE, COALESCE(ml.USR$RATE, 0) AS Rate, ml.USR$LISTNUMBER, COALESCE(ml.USR$MSALARY, 0) AS MSalary, COALESCE(ml.USR$PAYFORMKEY, 0) AS PayFormKey, (SELECT id FROM gd_ruid WHERE xid = 147009181 AND dbid = 119619099) AS SalaryKey, COALESCE(ml.USR$TSALARY, 0) AS TSalary, 8 * COALESCE(USR$THOURRATE, 0) AS AvgWageRate FROM USR$WG_MOVEMENTLINE ml WHERE ml.USR$EMPLKEY = 154242641 AND ml.USR$FIRSTMOVE = 154242645 ORDER BY ml.USR$EMPLKEY, ml.USR$FIRSTMOVE, ml.USR$DATEBEGIN "]).
param_list(wg_struct_sick,run,[pEmplKey-154242641,pFirstMoveKey-154242645,pQuery-usr_wg_TblCalDay/9,pSQL-"SELECT ml.USR$EMPLKEY, ml.USR$FIRSTMOVE AS FirstMoveKey, tcd.THEDAY, EXTRACT(YEAR FROM tcd.THEDAY) AS WYEAR, EXTRACT(MONTH FROM tcd.THEDAY) AS WMONTH, EXTRACT(DAY FROM tcd.THEDAY) AS WDAY, tcd.WDURATION, tcd.WORKDAY, ml.USR$SCHEDULEKEY FROM ( SELECT DISTINCT USR$EMPLKEY, USR$FIRSTMOVE, USR$SCHEDULEKEY FROM USR$WG_MOVEMENTLINE WHERE USR$EMPLKEY = 154242641 AND USR$FIRSTMOVE = 154242645 ) ml JOIN WG_TBLCAL tc ON tc.ID = ml.USR$SCHEDULEKEY JOIN WG_TBLCALDAY tcd ON tcd.TBLCALKEY = tc.ID WHERE COALESCE(tcd.WDURATION, 0) > 0 AND (tcd.THEDAY >= '2014-02-07' OR tcd.THEDAY >= '2014-02-07') AND (tcd.THEDAY < '2014-02-21' OR tcd.THEDAY < '2014-02-21') ORDER BY tcd.THEDAY "]).
param_list(wg_struct_sick,run,[pEmplKey-154242641,pFirstMoveKey-154242645,pQuery-usr_wg_TblCal_FlexLine/68,pSQL-"SELECT CASE gd.DOCUMENTTYPEKEY WHEN (SELECT id FROM gd_ruid WHERE xid = 147567935 AND dbid = 1514418708) THEN 'plan' WHEN (SELECT id FROM gd_ruid WHERE xid = 187613422 AND dbid = 1596169984) THEN 'fact' ELSE 'unknown' END AS FlexType, tcfl.USR$EMPLKEY, tcfl.USR$FIRSTMOVEKEY, EXTRACT(YEAR FROM t.USR$DATEBEGIN) AS CalYear, EXTRACT(MONTH FROM t.USR$DATEBEGIN) AS CalMonth, t.USR$DATEBEGIN, tcfl.USR$S1, tcfl.USR$H1, tcfl.USR$S2, tcfl.USR$H2, tcfl.USR$S3, tcfl.USR$H3, tcfl.USR$S4, tcfl.USR$H4, tcfl.USR$S5, tcfl.USR$H5, tcfl.USR$S6, tcfl.USR$H6, tcfl.USR$S7, tcfl.USR$H7, tcfl.USR$S8, tcfl.USR$H8, tcfl.USR$S9, tcfl.USR$H9, tcfl.USR$S10, tcfl.USR$H10, tcfl.USR$S11, tcfl.USR$H11, tcfl.USR$S12, tcfl.USR$H12, tcfl.USR$S13, tcfl.USR$H13, tcfl.USR$S14, tcfl.USR$H14, tcfl.USR$S15, tcfl.USR$H15, tcfl.USR$S16, tcfl.USR$H16, tcfl.USR$S17, tcfl.USR$H17, tcfl.USR$S18, tcfl.USR$H18, tcfl.USR$S19, tcfl.USR$H19, tcfl.USR$S20, tcfl.USR$H20, tcfl.USR$S21, tcfl.USR$H21, tcfl.USR$S22, tcfl.USR$H22, tcfl.USR$S23, tcfl.USR$H23, tcfl.USR$S24, tcfl.USR$H24, tcfl.USR$S25, tcfl.USR$H25, tcfl.USR$S26, tcfl.USR$H26, tcfl.USR$S27, tcfl.USR$H27, tcfl.USR$S28, tcfl.USR$H28, tcfl.USR$S29, tcfl.USR$H29, tcfl.USR$S30, tcfl.USR$H30, tcfl.USR$S31, tcfl.USR$H31 FROM GD_DOCUMENT gd JOIN USR$WG_TBLCAL_FLEXLINE tcfl ON gd.ID = tcfl.DOCUMENTKEY JOIN USR$WG_TBLCAL_FLEX tcf ON tcf.DOCUMENTKEY = tcfl.MASTERKEY JOIN USR$WG_TOTAL t ON t.DOCUMENTKEY = tcf.USR$TOTALDOCKEY WHERE tcfl.USR$EMPLKEY = 154242641 AND tcfl.USR$FIRSTMOVEKEY = 154242645 AND t.USR$DATEBEGIN >= '2014-02-07' AND t.USR$DATEBEGIN < '2014-02-21' ORDER BY tcfl.USR$EMPLKEY, tcfl.USR$FIRSTMOVEKEY, t.USR$DATEBEGIN "]).
